mapper.select:
SELECT ic.issued_coupon_uuid, c.coupon_name, ic.status, ic.expiration_date FROM issued_coupon ic JOIN coupon c ON c.coupon_uuid = ic.coupon_uuid
WHERE ic.member_uuid = #{memberUUID}
AND status = #{status}

SELECT coupon_name, coupon_description
FROM coupon
WHERE coupon_uuid=#{couponUUID}
